import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { loginUser } from '../components/redux/features/AuthSlice';
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';
import './login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const authState = useSelector((state) => state.data.auth);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      setError('Please fill in both fields.');
      return;
    }
    setLoading(true);
    try {
      await dispatch(loginUser({ email, password })).unwrap();
      navigate('/');
    } catch (err) {
      setError(authState.error || 'Invalid email or password. Please try again.');
      setLoading(false);
    }
  };

  return (
    <div className="auth-form-container">
      <h2>LOGIN</h2>
      <p>Please Enter your Email and Password!</p>

      <form className="login_form" onSubmit={handleSubmit}>
        {error && <p className="error-message">{error}</p>}
        
        <div className="input-group">
          <input
            type='email'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label>Email</label>
        </div>

        <div className="input-group">
          <input
            type='password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <label>Password</label>
        </div>

        <div>
          <button className='login_btn' type="submit">Login</button>
        </div>

        <div className="form-links">
          <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
          <p>Don't remember password? <Link to="/resetpassword">Reset Password</Link></p>
        </div>
      </form>

      
    </div>
  );
}

export default Login;
